import React, { useState, useEffect } from 'react'
import { KeyboardAvoidingView, View } from 'react-native'
import { ScrollView } from 'react-native'
import { StyleSheet, Text } from 'react-native'
import { Input, Button, Overlay } from 'react-native-elements'
import { db } from '../firebase'
import {
    AdMobBanner,
    setTestDeviceIDAsync
  } from "expo-ads-admob";

const settings = () => {
    const [pin, setPin] = useState()
    const [stream, setStream] = useState([])
    const [visible, setVisible] = useState(false);

    useEffect(() => {
        setTestDeviceIDAsync("EMULATOR");
    }, [])


    const Check = async () => {
        await setVisible(!visible);
        await db.collection('orders').where('pin', '==', pin).get().then((snapshot) => {
            snapshot.forEach(doc => {
                setStream("Your unique code is: " + doc.data().code)
            })
            setPin('')
        })
        await setVisible(visible)
    }

    return (
        <KeyboardAvoidingView>
            <ScrollView>
            <View
        style={{
          shadowOffset: { width: 5, height: 5 },
          width: "90%",
          borderRadius: 5,
          alignSelf: "center",
          alignContent: "center",
          alignItems: "center",
          marginTop: 10,
          marginBottom: 10,
        }}
      >
      <AdMobBanner
          bannerSize="smartBanner"
          adUnitID="ca-app-pub-1575625881370911/6730615952" 
          servePersonalizedAds // true or false
          onDidFailToReceiveAdWithError={(e) => console.log(e)}
          />
      </View>
                <Text>Information control center</Text>
                <Text style={{
                    marginTop: 20
                }}>Enter your pin here in order to view your unique code</Text>
                <Input value={pin} onChangeText={(text) => setPin(text)} style={{
                    marginTop: 30
                }} placeholder="Enter your pin" />

            </ScrollView>
            <View>
                <Button onPress={Check} title="Submit" />
                <Text style={{
                    marginTop: 40,
                }}>{stream}</Text>
            </View>
            <View>
                <Overlay isVisible={visible}>
                    <Text>Checking...</Text>
                </Overlay>
            </View>
        </KeyboardAvoidingView>

    )
}

export default settings

const styles = StyleSheet.create({})
